import { useEffect, useState } from 'react';
import './App.css';

function App() {
  const [backendMessage, setBackendMessage] = useState('');
  const [apiTest, setApiTest] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Test koneksi ke backend
    fetch('/api/test')
      .then(res => res.json())
      .then(data => {
        setApiTest(data.message);
        setLoading(false);
      })
      .catch(err => {
        console.error('Error:', err);
        setLoading(false);
      });

    // Test root endpoint
    fetch('/')
      .then(res => res.json())
      .then(data => setBackendMessage(data.message))
      .catch(err => console.error('Error:', err));
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <h1>ðŸš€ VibeCoding MERN Stack</h1>

        {loading ? (
          <p>Loading...</p>
        ) : (
          <div>
            <div style={{ margin: '20px', padding: '20px', background: '#282c34', borderRadius: '10px' }}>
              <h2>Backend Status:</h2>
              <p>âœ… {backendMessage}</p>
              <p>âœ… {apiTest}</p>
            </div>

            <p style={{ fontSize: '14px', color: '#61dafb' }}>
              Frontend: React.js | Backend: Express.js | Database: MongoDB Atlas
            </p>
          </div>
        )}
      </header>
    </div>
  );
}

export default App;
