import React, { useEffect, useRef, useState } from 'react';
import { scheduleAPI, intakeAPI, medicineAPI } from '../services/api';
import { useToast } from '../context/ToastContext';

// Local (client-side) notification scheduler for today's medicine times.
// Uses Web Notifications API; future push support can extend via service worker.
const NotificationManager = () => {
  const { addToast } = useToast();
  const timeoutsRef = useRef([]);
  const [enabled, setEnabled] = useState(() => {
    const saved = localStorage.getItem('notificationsEnabled');
    return saved === null ? true : saved === 'true';
  });

  const dayName = new Date().toLocaleDateString('en-US', { weekday: 'long' });

  useEffect(() => {
    if (!enabled) return; // Don't schedule if disabled
    requestPermission();
    loadAndSchedule();
    // Clean up timeouts on unmount
    return () => {
      timeoutsRef.current.forEach(t => clearTimeout(t));
      timeoutsRef.current = [];
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [enabled]);

  // Refresh schedule every hour for robustness
  useEffect(() => {
    if (!enabled) return;
    const interval = setInterval(loadAndSchedule, 60 * 60 * 1000);
    return () => clearInterval(interval);
  }, [enabled]);

  const requestPermission = () => {
    if (!('Notification' in window)) {
      addToast('Browser does not support notifications', 'error');
      return;
    }
    if (Notification.permission === 'default') {
      Notification.requestPermission().then(result => {
        if (result === 'granted') addToast('Notifications enabled', 'success');
        else addToast('Notifications permission denied', 'error');
      });
    }
  };

  const loadAndSchedule = async () => {
    try {
      // Clear previous timeouts
      timeoutsRef.current.forEach(t => clearTimeout(t));
      timeoutsRef.current = [];

      const [schedulesRes, todaysIntakesRes, medsRes] = await Promise.all([
        scheduleAPI.getAll(),
        intakeAPI.getTodayIntakes(),
        medicineAPI.getAll()
      ]);

      const takenMap = new Set(
        todaysIntakesRes.data.map(i => `${i.scheduleId || i.scheduleId?._id}-${new Date(i.takenAt).getHours()}:${new Date(i.takenAt).getMinutes()}`)
      );
      const medicinesMap = Object.fromEntries(medsRes.data.map(m => [m._id, m]));
      const now = new Date();

      const todaySchedules = schedulesRes.data.filter(s => {
        if (!s.days || s.days.length === 0) return true;
        return s.days.includes(dayName) || s.days.includes(dayName.substring(0,3));
      });

      todaySchedules.forEach(s => {
        const medicine = medicinesMap[s.medicineId?._id || s.medicineId] || s.medicine?.medicine || s.medicineId?.medicine || null;
        const timeStr = s.time || s.scheduledTime;
        if (!timeStr) return;
        // Parse HH:MM
        const [hh, mm] = timeStr.split(':');
        const scheduled = new Date();
        scheduled.setHours(parseInt(hh, 10), parseInt(mm, 10), 0, 0);
        if (scheduled <= now) return; // Past time
        const key = `${s._id}-${parseInt(hh,10)}:${parseInt(mm,10)}`;
        if (takenMap.has(key)) return; // Already taken around that time
        const delay = scheduled.getTime() - now.getTime();
        const timeoutId = setTimeout(() => {
          fireNotification(medicine, s, timeStr);
        }, delay);
        timeoutsRef.current.push(timeoutId);
      });
    } catch (err) {
      console.error('Failed to schedule notifications', err);
      addToast('Failed scheduling notifications', 'error');
    }
  };

  const fireNotification = (medicine, schedule, timeStr) => {
    const title = 'Medicine Reminder';
    const body = medicine
      ? `Time to take ${medicine.name} (${medicine.dosage || 'dosage'}) at ${timeStr}`
      : `Time for scheduled medicine at ${timeStr}`;
    if ('Notification' in window && Notification.permission === 'granted') {
      new Notification(title, {
        body,
        icon: '/favicon.ico'
      });
    }
    addToast(body, 'info');
  };

  const toggle = () => {
    const next = !enabled;
    setEnabled(next);
    localStorage.setItem('notificationsEnabled', String(next));
    addToast(next ? 'Notifications turned on' : 'Notifications turned off', next ? 'success' : 'error');
  };

  return (
    <div className="notifications-toggle" style={{ position: 'fixed', bottom: 18, right: 18, zIndex: 250 }}>
      <button
        onClick={toggle}
        className="nav-btn"
        style={{ background: enabled ? '#667eea' : '#f0f0f5', color: enabled ? '#fff' : '#333' }}
        aria-pressed={enabled}
      >
        {enabled ? 'ðŸ”” Notifications On' : 'ðŸ”• Notifications Off'}
      </button>
    </div>
  );
};

export default NotificationManager;
