import { useEffect, useState } from 'react';
import './App.css';

function App() {
  const [backendData, setBackendData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Test koneksi ke backend API
    fetch('/api/test')
      .then(res => res.json())
      .then(data => {
        setBackendData(data);
        setLoading(false);
      })
      .catch(err => {
        console.error('Error connecting to backend:', err);
        setError('Failed to connect to backend');
        setLoading(false);
      });
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <h1>üöÄ VibeCoding MERN Stack</h1>

        {loading ? (
          <p>Connecting to backend...</p>
        ) : error ? (
          <div style={{ color: 'red' }}>
            <p>‚ùå {error}</p>
            <p style={{ fontSize: '12px' }}>Make sure backend is running on port 5000</p>
          </div>
        ) : (
          <div>
            <div style={{ 
              margin: '20px', 
              padding: '20px', 
              background: '#1e1e1e', 
              borderRadius: '10px',
              border: '2px solid #61dafb'
            }}>
              <h2>‚úÖ Backend Connected!</h2>
              <p style={{ color: '#4ade80' }}>{backendData?.message}</p>
              <p style={{ fontSize: '12px', color: '#888' }}>
                Timestamp: {backendData?.timestamp ? new Date(backendData.timestamp).toLocaleString() : 'N/A'}
              </p>
            </div>

            <div style={{ marginTop: '30px' }}>
              <h3>Tech Stack:</h3>
              <div style={{ display: 'flex', gap: '20px', justifyContent: 'center', marginTop: '15px' }}>
                <div style={{ padding: '10px 20px', background: '#61dafb', color: '#000', borderRadius: '5px' }}>
                  React.js
                </div>
                <div style={{ padding: '10px 20px', background: '#68a063', color: '#fff', borderRadius: '5px' }}>
                  Express.js
                </div>
                <div style={{ padding: '10px 20px', background: '#4db33d', color: '#fff', borderRadius: '5px' }}>
                  MongoDB
                </div>
              </div>
            </div>

            <p style={{ fontSize: '14px', color: '#666', marginTop: '30px' }}>
              üéâ Your MERN stack is ready! Time to build something awesome.
            </p>
          </div>
        )}
      </header>
    </div>
  );
}

export default App;
